import React, { useEffect, useState } from 'react'
import Navbar from '../components/Navbar'
import Fab from '../components/Fab/fab'
import styled from "styled-components";
import { API_COVID_GLOBAL } from "../constant/API_URL"
import Info from './info';

const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;
margin: 0 auto;
width: 100%;
`
const Wrapper = styled.div`
  width: 55%;
  text-align: center;
  padding: 10px;
  border: 1px solid black;
`;
const Title = styled.p`
  font-size: 15px;
  font-weight: bold;
  font-family: Arial, Helvetica, sans-serif;
`;
const Source = styled.p`
  font-size: 13px;
  font-family: Arial, Helvetica, sans-serif;
  font-style: italic;
  margin-top: -10px;
`;

const Link = styled.a`
  color: black;
  text-decoration: none;
`;

const global = ({ data }) => {
  const [ showInfo, setShowInfo] = useState(true);
  const [ isNotif, setNotif] = useState(true)

  useEffect(() => {
    console.log("data covid", data.articles)
  }, [])

  const handleShowInfo = () => {
    setShowInfo(!showInfo);
    setNotif(!isNotif);
  }
  return (
    <Container>
      <Navbar valueDarkMode={darkMode} />
      <h1>Covid News - Global</h1>
      {
       showInfo ? (
          data.articles.map(data => (
            <Wrapper>
               <Link href={data.url} target="_blank">
                <Title>{data.title}</Title>
                <Source>{data.source.name}</Source>
              </Link>
            </Wrapper>
          ))
        ) : (<Info handlebtn={handleShowInfo} />)
      }
      <Fab handlebtn={handleShowInfo} isNotif={isNotif} valueDarkMode={darkMode} />
    </Container>
  )
}

export const getServerSideProps = async () => {
  try {
    const res = await fetch(`${API_COVID_GLOBAL}`)
    const data = await res.json()
    return {
      props:
        { data }
    }
  } catch (error) {
   return {
     props : {}
   }
}
}

// 1. klik fab = navbar background hitam tulisan putih
// 2. close button di klik = normal
export default global
