  //       console.log("error");
  //     }

  //   }

  // }

  // useEffect(() => {
  //   getDataCovid()

  //   Axios.get(API_COVID_INDO).then(response => console.log(response.data.articles))
  // }, [])

  return (
    <>
      {
        errorCode === 200 ? (
          <>
            <Container>
              <h1>Covid News - Indonesia</h1>
              <Navbar />
              {
                dataCovid.map(data => (
                  <Wrapper>
                    <Title>{data.title}</Title>
                    <Source>{data.source.name}</Source>
                  </Wrapper>
                )
                )
              }
              <Fab />
            </Container>
          </>
        ) : (<h1> Maaf Error gan </h1>)
      }
    </>
  )
}

export default indonesia;
