import React, { useState, useEffect } from 'react'
import Navbar from '../components/Navbar';
import Fab from '../components/Fab/fab';
import Info from "./info"
import styled from "styled-components";
import { API_COVID_INDO } from '../constant/API_URL';

const Container = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
margin: 0 auto;
width: 100%;
`
const Wrapper = styled.div`
  width: 55%;
  text-align: center;
  padding: 10px;
  border: 1px solid black;
`;
const Title = styled.p`
  font-size: 15px;
  font-weight: bold;
  font-family: Arial, Helvetica, sans-serif;
`;
const Source = styled.p`
  font-size: 13px;
  font-family: Arial, Helvetica, sans-serif;
  font-style: italic;
  margin-top: -10px;
`;

const Link = styled.a`
  color: black;
  text-decoration: none;
`;

const indonesia = ({ data }) => {
  const [closeBtn, setCloseBtn] = useState(true);

  useEffect(() => {
    console.log("data covid indo", data.articles)
  }, [])

  const ShowInfo = () => {
    setCloseBtn(!closeBtn);
  }

  // 1. list indonesia
  // 2. card info
  // 3. FAB
  // 4. Close Button
  return (
    <Container>
      <Navbar />
      <h1>Covid News - Indonesia</h1>
      {
        data.articles.map(data => (
          <Wrapper>
            <Link href={data.url} target="_blank">
              <Title>{data.title}</Title>
              <Source>{data.source.name}</Source>
            </Link>
       ``   </Wrapper>
        ))
      }
      {
        closeBtn ? (<Fab handlebtn={ShowInfo} />) :
          (<Info handlebtn={ShowInfo} />)
      }
    ``
    </Container>
  )
}

export const getServerSideProps = async () => {
  try {
    const res = await fetch(`${API_COVID_INDO}`)
    const data = await res.json()
    return {
      props:
        { data }
    }
  } catch (error) {
    return {
      props: {}
    }
  }
}

export default indonesia;
