const mapStateToProps = (state) => {
  return {
    showInfo: state.showInfo
  }
}

const mapDispatchToProps = {}

// 1. klik fab = navbar background hitam tulisan putih
// 2. close button di k

export default connect(mapStateToProps, mapDispatchToProps)(global)
