import React, { useState, useEffect } from 'react'
import Navbar from '../components/Navbar';
import Fab from '../components/Fab/fab';
import styled from "styled-components";
import Axios from 'axios';
import { API_COVID_INDO } from '../constant/API_URL';

const Container = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
margin: 0 auto;
width: 100%;
`
const Wrapper = styled.div``;
const Title = styled.p``;
const Source = styled.p``;

const indonesia = () => {
  const [errorCode, setErrorCode] = useState(200)
  const [dataCovid, setDataCovid] = useState([])

  const getDataCovid = async () => {
    try {
      const rawData = await axios.get(`${API_COVID_INDO}`)

      this.setState({
        dataCovid: rawData.data.articles
      })

    } catch (error) {

      if (error.response.status === 500) {
        console.log("error");
      }

    }

  }

  useEffect (() => {
   getDataCovid ()

   axios.get(API_COVID_INDO).then(response => console.log(response.data.articles))
  },[])

    return (
      <Container>
        <h1>Covid News - Indonesia</h1>
        <Navbar />
        <Wrapper>
          <Title></Title>
          <Source></Source>
        </Wrapper>
        <Fab />
      </Container>
    )
  }

export default indonesia;
