import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import styled from "styled-components"
import DataAlamat from "../form-formik/dataAlamat"
import { connect } from "react-redux"

const Wrapper = styled.form`
  display: flex;
  align-items: center;
  flex-direction: column;
`
const ListForm = styled.div`
  padding: 8px; 
`
const Error = styled.div`
  color: red;
`
const WrapBtn = styled.div`
  margin: 10px;
`

const BtnSubmit = styled.button`
  background-color: grey ;
  padding: 10px 15px ;
  cursor: pointer;
  margin-right: 15px;
  margin-left: 15px;
`
const InputForm = styled.input`
  width: 400px;
  padding: 6px 12px;
  font-size: 14px;
`

const LabelForm = styled.label`
  font-weight: bold;
  display: flex;
  margin-bottom: 5px;
`
const reqNumber = /^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/

const validationSchema = Yup.object({
  nama: Yup.string().required('Harus diisi!').min(3, 'Harus 3 karakter atau lebih!'),
  email: Yup.string().required('Harus diisi!').email('Format email tidak valid!'),
  nomor: Yup.string().required('Harus diisi!').matches(reqNumber, 'Nomor telepon tidak valid '),
});

const initialValues = {
  nama: '',
  email: '',
  Nomor: ''
}

const onSubmit = values => {
  console.log("Form values", values);
}

const DataDiri = (props) => {
  const formik = useFormik({
    initialValues,
    onSubmit,
    validationSchema,
  })

  return (
    <div>
      <Wrapper>
        <ListForm>
          <LabelForm htmlFor="nama">Nama Lengkap</LabelForm>
          <InputForm
            type="text"
            id="nama"
            name="nama"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.nama}

          />
          {formik.touched.nama && formik.errors.nama ? (
            <Error>{formik.errors.nama}</Error>
          ) : null}
        </ListForm>

        <ListForm>
          <LabelForm htmlFor="email">E-mail</LabelForm>
          <InputForm
            type="email"
            id="email"
            name="email"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.email}
          />
          {formik.touched.email && formik.errors.email ? (
            <Error>{formik.errors.email}</Error>
          ) : null}
        </ListForm>

        <ListForm>
          <LabelForm htmlFor="nomor">Nomor Telepon</LabelForm>
          <InputForm
            type="nomor"
            id="nomor"
            name="nomor"
            onChange={formik.handleChange}
            onBlur={formik.handleBlur}
            value={formik.values.nomor}
          />
          {formik.touched.nomor && formik.errors.nomor ? (
            <Error>{formik.errors.nomor}</Error>
          ) : null}
        </ListForm>
        <WrapBtn>
          <BtnSubmit onClick={props.handleShowInfo} type="submit"> Lanjut </BtnSubmit>
          {
            props.isNotif ? (null) : (<DataAlamat />)
          }
        </WrapBtn>
      </Wrapper>
    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    isNotif: state.navbarReducer.isNotif
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    handleShowInfo: () => dispatch({ type: HANDLE_SHOW_INFO })
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(DataDiri)
